import "../assets/css/galeria.css";
import Heart from "./Heart";
import MyContext from "../MyContext";
import { useContext} from "react";

export default function Home() {
  const {allPhotos, setAllPhotos} = useContext(MyContext);
  const likedPhotos = (id) => {
    const indexPhoto =  allPhotos.findIndex(photo => photo.id == id);
    allPhotos[indexPhoto].liked = !allPhotos[indexPhoto].liked;
    setAllPhotos([...allPhotos]);
  };

  return (
    <div className="galeria grid-columns-5 p-3">
            {allPhotos.map((photo, i) => {
              return (
                <div className="foto" key={i} style={{backgroundImage: `url(${photo.src.tiny})`}} onClick={() => {likedPhotos(photo.id)}}>
                <Heart filled={photo.liked} />
                <p>{photo.alt}</p>
                </div>
              )
            })};
    </div>
  );
};
